
var indice = 0;
frases = new Array();
frases[0] = "Texto1";
frases[1] = "Texto2";
frases[2] = "Texto3";
frases[3] = "Texto4";

indice = Math.random()*(frases.length);
indice = Math.floor(indice);

function rotar() {
if (indice == frases.length) {indice = 0;}
document.getElementById("rotando").innerHTML = frases[indice];
indice++;
setTimeout("rotar();",5000);
}


